style.transform = `translateX(${translateX}px)`; // Update navigation buttons (always enabled for infinite scroll) prevBtn.disabled = false; nextBtn.disabled = false; // Update indicators based on logical position const logicalIndex = currentIndex % originalItems; indicators.forEach((indicator, index) => { indicator.classList.toggle('bg-blue-500', index === logicalIndex); indicator.classList.toggle('bg-gray-300', index !== logicalIndex); }); } function nextSlide() { currentIndex++; updateCarousel(); // If we've moved past the original items, reset to the beginning seamlessly if (currentIndex >= originalItems) { setTimeout(() => { currentIndex = 0; updateCarousel(true); // Instant transition }, 500); // Wait for transition to complete } } function prevSlide() { // If we're at the beginning, jump to the end of original items first if (currentIndex <= 0) { currentIndex = originalItems; updateCarousel(true); // Instant transition setTimeout(() => { currentIndex = originalItems - 1; updateCarousel(); }, 50); } else { currentIndex--; updateCarousel(); } } function goToSlide(index) { currentIndex = index; updateCarousel(); } function startAutoPlay() { autoPlayInterval = setInterval(nextSlide, 5000); } function stopAutoPlay() { clearInterval(autoPlayInterval); } function resetAutoPlay() { stopAutoPlay(); startAutoPlay(); } // Event listeners nextBtn.addEventListener('click', () => { nextSlide(); resetAutoPlay(); }); prevBtn.addEventListener('click', () => { prevSlide(); resetAutoPlay(); }); // Indicator clicks indicators.forEach((indicator, index) => { indicator.addEventListener('click', () => { goToSlide(index); resetAutoPlay(); }); }); // Touch/swipe support for mobile let startX = 0; let startY = 0; let isDragging = false; carousel.addEventListener('touchstart', (e) => { startX = e.touches[0].clientX; startY = e.touches[0].clientY; isDragging = true; }); carousel.addEventListener('touchmove', (e) => { if (!isDragging) return; e.preventDefault(); }); carousel.addEventListener('touchend', (e) => { if (!isDragging) return; isDragging = false; const endX = e.changedTouches[0].clientX; const endY = e.changedTouches[0].clientY; const diffX = startX - endX; const diffY = startY - endY; // Only handle horizontal swipes if (Math.abs(diffX) > Math.abs(diffY) && Math.abs(diffX) > 50) { if (diffX > 0) { nextSlide(); } else { prevSlide(); } resetAutoPlay(); } }); // Keyboard navigation document.addEventListener('keydown', (e) => { if (carousel.matches(':hover') || document.activeElement === carousel) { if (e.key === 'ArrowLeft') { e.preventDefault(); prevSlide(); resetAutoPlay(); } else if (e.key === 'ArrowRight') { e.preventDefault(); nextSlide(); resetAutoPlay(); } } }); // Pause on hover, resume on leave carousel.addEventListener('mouseenter', stopAutoPlay); carousel.addEventListener('mouseleave', startAutoPlay); // Initialize updateCarousel(); startAutoPlay(); // Handle window resize window.addEventListener('resize', () => { updateCarousel(true); }); }); </script> <!-- Floating Online Staff Widget (Right Edge) --> <div id="online-staff-widget" class="fixed right-0 top-1/2 transform -translate-y-1/2 z-50"> <!-- Collapsed State --> <div id="staff-widget-collapsed" class="bg-white shadow-lg border border-gray-200 rounded-l-xl"> <button onclick="toggleStaffWidget()" class="p-3 focus:outline-none hover:bg-gray-50 transition-colors"> <div class="flex flex-col items-center space-y-2"> <!-- Online indicator --> <div class="relative"> <div class="w-8 h-8 bg-gradient-to-br from-green-500 to-emerald-600 rounded-lg flex items-center justify-center"> <svg class="w-4 h-4 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"> <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M17 20h5v-2a3 3 0 00-5.356-1.857M17 20H7m10 0v-2c0-.656-.126-1.283-.356-1.857M7 20H2v-2a3 3 0 015.356-1.857M7 20v-2c0-.656.126-1.283.356-1.857m0 0a5.002 5.002 0 019.288 0M15 7a3 3 0 11-6 0 3 3 0 016 0zm6 3a2 2 0 11-4 0 2 2 0 014 0zM7 10a2 2 0 11-4 0 