add('hidden'); collapsed.classList.remove('hidden'); } else { // Show expanded, hide collapsed collapsed.classList.add('hidden'); expanded.classList.remove('hidden'); } } // Auto-collapse after 10 seconds of inactivity let widgetTimeout; function resetWidgetTimeout() { clearTimeout(widgetTimeout); widgetTimeout = setTimeout(() => { const collapsed = document.getElementById('staff-widget-collapsed'); const expanded = document.getElementById('staff-widget-expanded'); if (!collapsed.classList.contains('hidden')) return; // Already collapsed expanded.classList.add('hidden'); collapsed.classList.remove('hidden'); }, 10000); } // Reset timeout on any interaction with the widget document.getElementById('online-staff-widget').addEventListener('mouseenter', resetWidgetTimeout); document.getElementById('online-staff-widget').addEventListener('mouseleave', resetWidgetTimeout); document.getElementById('online-staff-widget').addEventListener('click', resetWidgetTimeout); </script> <style> .writing-mode-vertical { writing-mode: vertical-rl; text-orientation: mixed; } </style> @endsection 
