ntPolls->count() > 0) <div class="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden"> <div class="bg-gradient-to-r from-orange-50 to-red-50 px-6 py-4 border-b border-gray-200"> <h3 class="text-lg font-semibold text-gray-900">Recent Activity</h3> </div> <div class="p-6"> <div class="space-y-3"> @foreach($recentPolls as $recentPoll) <div class="flex items-center gap-3 p-2 rounded-lg hover:bg-gray-50 transition-colors"> <div class="w-2 h-2 bg-orange-400 rounded-full"></div> <div class="flex-1 min-w-0"> <p class="text-sm font-medium text-gray-900 truncate"> <a href="{{ route('polls.show', $recentPoll) }}" class="hover:text-blue-600"> {{ Str::limit($recentPoll->title, 30) }} </a> </p> <p class="text-xs text-gray-500">{{ $recentPoll->created_at->diffForHumans() }}</p> </div> </div> @endforeach </div> </div> </div> @endif </div> </div> <!-- Quick Poll Modal --> @auth <div id="quickPollModal" class="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50 hidden"> <div class="relative top-20 mx-auto p-5 border w-full max-w-2xl shadow-lg rounded-xl bg-white"> <div class="mt-3"> <!-- Modal Header --> <div class="flex items-center justify-between pb-4 border-b border-gray-200"> <h3 class="text-2xl font-bold text-gray-900">Quick Poll</h3> <button onclick="closeQuickPollModal()" class="text-gray-400 hover:text-gray-600 transition-colors"> <svg class="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24"> <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path> </svg> </button> </div> <!-- Modal Body --> <form action="{{ route('polls.store') }}" method="POST" class="mt-6"> @csrf <!-- Quick Poll Title --> <div class="mb-6"> <label for="quick_title" class="block text-sm font-medium text-gray-700 mb-2"> Poll Question <span class="text-red-500">*</span> </label> <input type="text" id="quick_title" name="title" required placeholder="What would you like to ask?" class="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"> </div> <!-- Poll Type Selection --> <div class="mb-6"> <label class="block text-sm font-medium text-gray-700 mb-3">Poll Type</label> <div class="grid grid-cols-2 gap-4"> <label class="relative"> <input type="radio" name="type" value="yes_no" checked class="sr-only"> <div class="poll-type-card p-4 border-2 border-gray-200 rounded-lg cursor-pointer hover:border-blue-300 transition-colors"> <div class="text-center"> <div class="text-2xl mb-2">✅</div> <div class="font-medium text-gray-900">Yes/No</div> <div class="text-sm text-gray-500">Simple binary choice</div> </div> </div> </label> <label class="relative"> <input type="radio" name="type" value="single_choice" class="sr-only"> <div class="poll-type-card p-4 border-2 border-gray-200 rounded-lg cursor-pointer hover:border-blue-300 transition-colors"> <div class="text-center"> <div class="text-2xl mb-2">📊</div> <div class="font-medium text-gray-900">Multiple Options</div> <div class="text-sm text-gray-500">Choose one option</div> </div> </div> </label> </div> </div> <!-- Options for Multiple Choice (hidden by default) --> <div id="optionsSection" class="mb-6 hidden"> <label class="block text-sm font-medium text-gray-700 mb-2">Options</label> <div id="optionsList" class="space-y-2"> <input type="text" name="options[]" placeholder="Option 1" class="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"> <input type="text" name="options[]" placeholder="Option 2" class="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"> </div> <button type="button" onclick="addOption()" class="mt-2 text-sm text-blue-600 hover:text-blue-700"> + Add another option </button> </div> <!-- Quick Settings --> <div class="mb-6"> <label class="block text-sm font-medium text-gray-700 mb-3">Quick Settings</label> <div class="space-y-3"> <label class="flex items-center"> <input type="checkbox" name="anonymous" class="rounded border-gray-300 text-blue-600 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"> <span class="ml-2 text-sm text-gray-700">Anonymous voting</span> </label> <label class="flex items-center"> <input type="checkbox" name="show_results" checked class="rounded border-gray-300 text-blue-600 shadow-sm focus:border-blue-300 focus:ring focus:ring-blue-200 focus:ring-opacity-50"> <span class="ml-2 text-sm text-gray-700">Show results after voting</span> </label> </div> </div> <!-- Hidden Fields --> <input type="hidden" name="visibility" value="public"> <input type="hidden" name="status" value="active"> <!-- Modal Footer --> <div class="flex items-center justify-end gap-3 pt-4 border-t border-gray-200"> <button type="button" onclick="closeQuickPollModal()" class="px-6 py-2 bg-gray-100 hover:bg-gray-200 text-gray-700 text-sm font-medium rounded-lg transition-colors"> Cancel </button> <button type="submit" class="px-6 py-2 bg-blue-600 hover:bg-blue-700 text-white text-sm font-medium rounded-lg transition-colors"> Create Poll </button> </div> </form> </div> </div> </div> @endauth <!-- Success/Error Messages --> @if(session('success')) <div class="fixed bottom-4 right-4 bg-green-100 border border-green-400 text-green-700 px-6 py-4 rounded-lg shadow-lg z-50" id="successMessage"> <div class="flex items-center"> <svg class="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24"> <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 13l4 4L19 7"></path> </svg> {{ session('success') }} </div> </div> @endif @if(session('error')) <div class="fixed bottom-4 right-4 bg-red-100 border border-red-400 text-red-700 px-6 py-4 rounded-lg shadow-lg z-50" id="errorMessage"> <div class="flex items-center"> <svg class="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24"> <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M6 18L18 6M6 6l12 12"></path> </svg> {{ session('error') }} </div> </div> @endif @endsection @push('scripts') <script> // Quick Poll Modal Functions function openQuickPollModal() { document.getElementById('quickPollModal').classList.remove('hidden'); document.body.style.overflow = 'hidden'; } function closeQuickPollModal() { document.getElementById('quickPollModal').classList.add('hidden'); document.body.style.overflow = 'auto'; // Reset form document.querySelector('#quickPollModal form').reset(); document.getElementById('optionsSection').classList.add('hidden'); // Reset poll type selection document.querySelectorAll('.poll-type-card').forEach(card => { card.classList.remove('border-blue-500', 'bg-blue-50'); card.classList.add('border-gray-200'); }); document.querySelector('input[value="yes_no"]').checked = true; document.querySelector('input[value="yes_no"]').closest('.poll-type-card').classList.add('border-blue-500', 'bg-blue-50'); } // Handle poll type selection document.addEventListener('DOMContentLoaded', function() { const typeInputs = document.querySelectorAll('input[name="type"]'); const optionsSection = document.getElementById('optionsSection'); typeInputs.forEach(input => { input.addEventListener('change', function() { // Update visual selection document.querySelectorAll('.poll-type-card').forEach(card => { card.classList.remove('border-blue-500', 'bg-blue-50'); card.classList.add('border-gray-200'); }); this.closest('.poll-type-card').classList.remove('border-gray-200'); this.closest('.poll-type-card').classList.add('border-blue-500', 'bg-blue-50'); // Show/hide options section if (this.value === 'single_choice') { optionsSection.classList.remove('hidden'); } else { optionsSection.classList.add('hidden'); } }); }); // Initialize first option as selected document.querySelector('input[value="yes_no"]').closest('.poll-type-card').classList.add('border-blue-500', 'bg-blue-50'); }); // Add option function function addOption() { const optionsList = document.getElementById('optionsList'); const optionCount = optionsList.children.length + 1; if (optionCount <= 10) { const newOption = document.createElement('input'); newOption.type = 'text'; newOption.name = 'options[]'; newOption.placeholder = `Option ${optionCount}`; newOption.className = 'w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500'; optionsList.appendChild(newOption); newOption.focus(); } } // Auto-hide success/error messages setTimeout(function() { const successMessage = document.getElementById('successMessage'); const errorMessage = document.getElementById('errorMessage'); if (successMessage) { successMessage.style.opacity = '0'; setTimeout(() => successMessage.remove(), 300); } if (errorMessage) { errorMessage.style.opacity = '0'; setTimeout(() => errorMessage.remove(), 300); } }, 5000); // Close modal on escape key document.addEventListener('keydown', function(e) { if (e.key === 'Escape') { closeQuickPollModal(); } }); // Close modal on outside click document.getElementById('quickPollModal')?.addEventListener('click', function(e) { if (e.target === this) { closeQuickPollModal(); } }); </script> @endpush 
