ton.innerHTML; // Show loading state button.innerHTML = ` <div class="w-8 h-8 border-2 border-gray-300 rounded-full flex items-center justify-center"> <div class="w-4 h-4 border-2 border-indigo-600 border-t-transparent rounded-full animate-spin"></div> </div> `; button.disabled = true; fetch(`/todos/${todoId}/toggle`, { method: 'PATCH', headers: { 'X-CSRF-TOKEN': document.querySelector('meta[name="csrf-token"]').getAttribute('content'), 'Content-Type': 'application/json', }, }) .then(response => response.json()) .then(data => { if (data.success) { // Show success state button.innerHTML = ` <div class="w-8 h-8 bg-green-500 rounded-full flex items-center justify-center shadow-lg"> <svg class="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"> <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2.5" d="M5 13l4 4L19 7"></path> </svg> </div> `; setTimeout(() => { location.reload(); }, 1000); } else { button.innerHTML = originalContent; button.disabled = false; alert('Something went wrong. Please try again.'); } }) .catch(error => { console.error('Error:', error); button.innerHTML = originalContent; button.disabled = false; alert('Network error. Please try again.'); }); } </script> @endsection
